var room = 1;

function education_fields() {

    room++;
    var objTo = document.getElementById('education_fields')
    var divtest = document.createElement("div");
    divtest.setAttribute("class", "form-group removeclass" + room);
    var rdiv = 'removeclass' + room;
    divtest.innerHTML = ' <div class="row"> <div class="col-lg-6"> <div class="form-group"> <select class="select2 form-control custom-select" style="width: 100%; height:36px;"> <option>Select</option> <optgroup label="Alaskan/Hawaiian Time Zone"> <option value="AK">Alaska</option> <option value="HI">Hawaii</option> </optgroup> <optgroup label="Pacific Time Zone"> <option value="CA">California</option> <option value="NV">Nevada</option> </optgroup> <optgroup label="Mountain Time Zone"> <option value="AZ">Arizona</option> <option value="CO">Colorado</option> </optgroup> <optgroup label="Central Time Zone"> <option value="AL">Alabama</option> <option value="AR">Arkansas</option> </optgroup> <optgroup label="Eastern Time Zone"> <option value="CT">Connecticut</option> <option value="DE">Delaware</option> </optgroup> </select> </div></div><div class="col-lg-3 switchery-demo" style="margin-top: -40px;"> <p class="font-weight-bolder">Capture</p><label class="switch"> <input type="checkbox"> <span class="slider v2"></span> </label> </div><div class="col-lg-3 switchery-demo" style="margin-top: -40px; margin-left: -75px;"> <p class="font-weight-bolder">Release</p><label class="switch"> <input type="checkbox"> <span class="sliderV2 v2"></span> </label> <button class="btn btn-danger" type="button" onclick="remove_education_fields(' + room + ');" style="margin-left: 80px; margin-top: -75px"><i class="fa fa-minus"></i></button> </div></div>';
    objTo.appendChild(divtest)
}

function remove_education_fields(rid) {
    $('.removeclass' + rid).remove();
}

